$(document).ready(function () {
    const headerEl = document.querySelector("header");
    const headerHref = document.querySelectorAll("nav ul li a");
    const headerTop = headerEl.offsetTop;
    const navItems = document.querySelectorAll("nav ul li");
    const headerName = document.querySelector("h2");
    console.log("navItems", navItems);
    console.log(headerTop + 20);
    console.log(window.scrollY);

    const stickyHandler = () => {
      if (window.scrollY >= headerTop + 10) {
        headerEl.classList.add("sticky");
        navItems.forEach((item) => {
          item.classList.add("item-scroll");
        });
        headerHref.forEach((item) => {
          item.classList.add("item-scroll");
        });
        headerName.classList.add("name-scroll");
      } else {
        headerEl.classList.remove("sticky");
        navItems.forEach((item) => {
          item.classList.remove("item-scroll");
        });
        headerHref.forEach((item) => {
          item.classList.remove("item-scroll");
        });
        headerName.classList.remove("name-scroll");

      }
    };
    window.addEventListener("scroll", stickyHandler);

    $("#exp").hide();
    $("#skill").hide();
    $("#btn-inform").on("click", function () {
      $("#skill").hide();
      $("#exp").hide();
      $("#infor").show();
    });
    $("#btn-skill").on("click", function () {
      $("#infor").hide();
      $("#exp").hide();
      $("#skill").show();
    });
    $("#btn-exp").on("click", function () {
      $("#skill").hide();
      $("#infor").hide();
      $("#exp").show();
    });
  });
  $('.project-percent').each(function(){
    var $this = $(this);
    var percent = $this.attr('percent');
    $this.css("width",percent+'%');
    $({animatedValue: 0}).animate({animatedValue: percent},{
        duration: 500,
        step: function(){
            $this.attr('percent', Math.floor(this.animatedValue) + '%');
        },
        complete: function(){
            $this.attr('percent', Math.floor(this.animatedValue) + '%');
        }
    });
});
  $('#main').click(function () {
    var position = document.body.scrollTop || document.documentElement.scrollTop;
    if (position) {
      window.scrollBy(0, -Math.max(1, Math.floor(position / 10)));
      scrollAnimation = setTimeout("scrollToTop()", 30);
    } else clearTimeout(scrollAnimation);
  });
  function showInform() {
    document.getElementById("image").src = "https://1.bp.blogspot.com/-JGfr6aMx1cE/YFoLYh5q88I/AAAAAAAAD0A/sZDgvSWR-SAFuN4hKTAiN0ZO7hxHmuNpwCLcBGAsYHQ/s934/pngfind.com-blackpink-png-1839440.png";
  }
  function showSkill() {
    document.getElementById("image").src = "https://1.bp.blogspot.com/-A2OaDl1Yh4k/YFoLYnxZoeI/AAAAAAAADz8/irm55-A2rAIIZkJa3_Y1Fzd160BWS2OewCLcBGAsYHQ/s965/pngfind.com-blackpink-png-901367.png";
  }
  function showExp() {
    document.getElementById("image").src = "https://1.bp.blogspot.com/-ZphDIYeLa30/YFoLYq1sZDI/AAAAAAAADz4/HFXGKN8c_YIKPFYJWAvzJGHXuKyUKriCwCLcBGAsYHQ/s573/pngfind.com-blackpink-png-1839410.png";
  }
